// SPDX-License-Identifier: MIT
pragma solidity 0.8.21;

import {AbstractModule} from "../abstracts/AbstractModule.sol";
import {AttestationPayload} from "../interfaces/Structs.sol";
import {IPortalRegistry} from "../interfaces/IPortalRegistry.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title Rubyscore Fee Module
 * @notice This module can be used by portal creators to enforce a fee on attestations.
 */
contract RubyscoreFeeModule is AbstractModule, Ownable {
    mapping(bytes32 => uint256) public attestationFees;

    /// @notice Error thrown when an array length mismatch occurs
    error ArrayLengthMismatch();
    /// @notice Error thrown when an invalid attestation fee is provided
    error InvalidAttestationFee();
    error ZeroAddressCheck();

    event FeesSet(bytes32[] schemaIds, uint256[] attestationFees);

    /**
     * @notice Contract constructor sets the portal registry
     */
    constructor(address initialOwner) Ownable(initialOwner) {}

    /**
     * @notice Set the fee required to attest
     * @param _attestationFees The fees required to attest
     * @param schemaIds The schemaIds to set the fee for
     */
    function setFees(bytes32[] memory schemaIds, uint256[] memory _attestationFees) public onlyOwner {
        if (schemaIds.length != _attestationFees.length) revert ArrayLengthMismatch();

        for (uint256 i = 0; i < schemaIds.length; i++) {
            attestationFees[schemaIds[i]] = _attestationFees[i];
        }

        emit FeesSet(schemaIds, _attestationFees);
    }

    /**
     * @notice The main method for the module, running the check
     * @param _value The value sent for the attestation
     */
    function run(
        AttestationPayload memory _attestationPayload,
        bytes memory /*_validationPayload*/,
        address /*_txSender*/,
        uint256 _value
    ) public view override {
        uint256 attestationFee = attestationFees[_attestationPayload.schemaId];
        if (_value < attestationFee) revert InvalidAttestationFee();
    }
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.21;

import {AbstractModule} from "../abstracts/AbstractModule.sol";
import {AttestationPayload} from "../interfaces/Structs.sol";
import {IPortalRegistry} from "../interfaces/IPortalRegistry.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IRubyscore_Certificates} from "../interfaces/IRubyscore_Certificates.sol";

/**
 * @title Rubyscore Issuance Module
 * @notice This module can be used for issues NFT certificates .
 */
contract RubyscoreIssuanceModule is AbstractModule, Ownable {
    address public portal;
    IRubyscore_Certificates public certificateNFT;

    mapping(bytes32 schemaId => uint256 certificateId) public certificates;

    /// @notice Error thrown when an array length mismatch occurs
    error ArrayLengthMismatch();
    error InvalidCertificateId();
    error ZeroAddressCheck();

    modifier onlyPortal() {
        require(msg.sender == portal, "Not portal");
        _;
    }

    /**
     * @notice Contract constructor sets the portal registry
     */
    constructor(address initialOwner, address _portal, IRubyscore_Certificates _certificateNFT) Ownable(initialOwner) {
        if (_portal == address(0)) revert ZeroAddressCheck();
        if (address(_certificateNFT) == address(0)) revert ZeroAddressCheck();
        portal = _portal;
        certificateNFT = _certificateNFT;
    }

    function setUpCertificates(bytes32[] memory schemaIds, uint256[] memory certificateIds) external onlyOwner {
        if (schemaIds.length != certificateIds.length) revert ArrayLengthMismatch();
        for (uint256 i = 0; i < schemaIds.length; i++) {
            certificates[schemaIds[i]] = certificateIds[i];
        }
    }

    function setPortal(address _portal) external onlyOwner {
        portal = _portal;
    }

    /**
     * @notice The main method for the module, running the check
     * @param _txSender Sender of transaction
     */
    function run(
        AttestationPayload memory _attestationPayload,
        bytes memory /*_validationPayload*/,
        address _txSender,
        uint256 /*_value*/
    ) public override {
        require(msg.sender == portal, "Only Ruby portal");
        uint256 certificateId = certificates[_attestationPayload.schemaId];
        if (certificateId == 0) revert InvalidCertificateId();
        certificateNFT.safeMint(_txSender, certificateId);
    }
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.21;

import {AbstractModule} from "../abstracts/AbstractModule.sol";
import {AttestationPayload} from "../interfaces/Structs.sol";
import {IPortalRegistry} from "../interfaces/IPortalRegistry.sol";
import {EIP712} from "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title Rubyscore Signature Check Module
 * @notice This module can be used by portal to
 *         require a signature from an authorized signer
 *         before issuing attestations.
 */
contract RubyscoreSignCheckModule is AbstractModule, EIP712, Ownable {
    using ECDSA for bytes32;

    string public constant NAME = "Rubyscore_SignCheckModule";
    string public constant VERSION = "0.0.1";

    address private signer;

    /// @notice Error thrown when an array length mismatch occurs
    error ArrayLengthMismatch();
    /// @notice Error thrown when a signer is not authorized by the module
    error SignerNotAuthorized();

    /// @notice Event emitted when the authorized signers are set
    event SignerAuthorized(address signer);

    /**
     * @notice Contract constructor sets the portal registry
     */
    constructor(address initialOwner, address _signer) EIP712(NAME, VERSION) Ownable(initialOwner) {
        require(_signer != address(0), "Zero address check");
        signer = _signer;
    }

    function getSigner() external view returns (address) {
        return signer;
    }

    /**
     * @notice Set the accepted status of schemaIds
     * @param _signer The signers to be set
     */
    function setAuthorizedSigners(address _signer) public onlyOwner {
        signer = _signer;
        emit SignerAuthorized(signer);
    }

    /**
     * @notice The main method for the module, running the check
     * @param _attestationPayload The Payload of the attestation
     * @param _validationPayload The validation payload required for the module
     */
    function run(
        AttestationPayload memory _attestationPayload,
        bytes memory _validationPayload,
        address _txSender,
        uint256 /*_value*/
    ) public view override {
        bytes32 digest = _hashTypedDataV4(
            keccak256(
                abi.encode(
                    keccak256(
                        "AttestationPayload(bytes32 schemaId,uint64 expirationDate,bytes subject,bytes attestationData)"
                    ),
                    _attestationPayload.schemaId,
                    _attestationPayload.expirationDate,
                    keccak256(abi.encode(_txSender)),
                    keccak256(_attestationPayload.attestationData)
                )
            )
        );
        if (signer != ECDSA.recover(digest, _validationPayload)) revert SignerNotAuthorized();
    }
}

contracts/certificates/Rubyscore_Certifi.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.21;

import {IRubyscore_Certificates} from "./interfaces/IRubyscore_Certificates.sol";
import {ERC1155URIStorage} from "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155URIStorage.sol";
import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {ERC1155, ERC1155Supply} from "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Supply.sol";

/**
 * @title Rubyscore_Certificates
 * @dev An ERC1155 token contract for minting and managing certificates with URI support.
 * @dev Rubyscore_Certificates can be minted by users with the MINTER_ROLE after proper authorization.
 * @dev Rubyscore_Certificates can have their URIs set by operators with the MINTER_ROLE.
 * @dev Rubyscore_Certificates can be safely transferred with restrictions on certain tokens.
 */

contract Rubyscore_Certificates is ERC1155, AccessControl, ERC1155Supply, ERC1155URIStorage, IRubyscore_Certificates {
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    string public name;
    string public symbol;

    /**
     * @dev See {IRubyscore_Certificates}
     */
    function supportsInterface(
        bytes4 interfaceId
    ) public view override(ERC1155, AccessControl, IRubyscore_Certificates) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    function totalSupply(uint256 id) public view override(ERC1155Supply) returns (uint256) {
        return super.totalSupply(id);
    }

    /**
     * @dev See {IRubyscore_Certificates}
     */
    function uri(
        uint256 tokenId
    ) public view override(ERC1155, ERC1155URIStorage, IRubyscore_Certificates) returns (string memory) {
        return super.uri(tokenId);
    }

    /**
     * @dev See {IRubyscore_Certificates}
     */
    function tokenURI(uint256 tokenId) public view returns (string memory) {
        return uri(tokenId);
    }

    /**
     * @notice Constructor for the Rubyscore_Achievement contract.
     * @dev Initializes the contract with roles and settings.
     * @param admin The address of the admin role, which has overall control.
     * @param operator The address of the operator role, responsible for unlock tokens and set base URI.
     * @param minter The address of the minter role, authorized to mint achievements and responsible for setting token URIs.
     * @param baseURI The base URI for token metadata.
     * @dev It sets the base URI for token metadata to the provided `baseURI`.
     * @dev It grants the DEFAULT_ADMIN_ROLE, OPERATOR_ROLE, and MINTER_ROLE to the specified addresses.
     * @dev It also initializes the contract with EIP712 support and ERC1155 functionality.
     */
    constructor(
        address admin,
        address operator,
        address minter,
        string memory baseURI,
        string memory _name,
        string memory _symbol
    ) ERC1155("ipfs://") {
        require(admin != address(0), "Zero address check");
        require(operator != address(0), "Zero address check");
        require(minter != address(0), "Zero address check");
        name = _name;
        symbol = _symbol;
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(OPERATOR_ROLE, msg.sender);
        _grantRole(OPERATOR_ROLE, operator);
        _grantRole(MINTER_ROLE, minter);
        _setBaseURI(baseURI);
    }

    /**
     * @dev See {IRubyscore_Certificates}
     */
    function setTokenURI(uint256 tokenId, string memory newTokenURI) public onlyRole(OPERATOR_ROLE) {
        super._setURI(tokenId, newTokenURI);
        emit TokenURISet(tokenId, newTokenURI);
    }

    /**
     * @dev See {IRubyscore_Certificates}
     */
    function setBatchTokenURI(
        uint256[] calldata tokenIds,
        string[] calldata newTokenURIs
    ) external onlyRole(OPERATOR_ROLE) {
        require(tokenIds.length == newTokenURIs.length, "Invalid params");
        for (uint256 i = 0; i < tokenIds.length; i++) {
            setTokenURI(tokenIds[i], newTokenURIs[i]);
        }
    }

    /**
     * @dev See {IRubyscore_Certificates}
     */
    function setBaseURI(string memory newBaseURI) external onlyRole(OPERATOR_ROLE) {
        super._setBaseURI(newBaseURI);
        emit BaseURISet(newBaseURI);
    }

    /**
     * @dev See {IRubyscore_Certificates}
     */
    function safeMint(address to, uint256 nftId) external payable onlyRole(MINTER_ROLE) {
        _mint(to, nftId, "");
        emit Minted(to, nftId);
    }

    /**
     * @dev See {IRubyscore_Certificates}
     */
    function safeBatchMint(address to, uint256[] memory nftIds) external payable onlyRole(MINTER_ROLE) {
        _mintBatch(to, nftIds, "");
        emit BatchMinted(to, nftIds);
    }

    /**
     * @dev See {IRubyscore_Certificates}
     */
    function _mint(address to, uint256 id, bytes memory data) internal {
        // require(balanceOf(to, id) == 0, "You already have this certificate");
        super._mint(to, id, 1, data);
    }

    /**
     * @notice Internal function to safely mint multiple NFTs in a batch for a specified recipient.
     * @param to The address of the recipient to mint the NFTs for.
     * @param ids An array of NFT IDs to mint.
     * @param data Additional data to include in the minting transaction.
     * @dev This function checks if the recipient already owns any of the specified NFTs to prevent duplicates.
     * @dev It is intended for batch minting operations where multiple NFTs can be minted at once.
     */
    function _mintBatch(address to, uint256[] memory ids, bytes memory data) internal {
        uint256[] memory amounts = new uint256[](ids.length);
        for (uint8 i = 0; i < ids.length; i++) {
            require(balanceOf(to, ids[i]) == 0, "You already have this certificate");
            amounts[i] = 1;
        }
        super._mintBatch(to, ids, amounts, data);
    }

    function _update(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory values
    ) internal override(ERC1155, ERC1155Supply) {
        for (uint256 i = 0; i < ids.length; i++) {
            if (from != address(0)) revert("This token only for you");
        }
        super._update(from, to, ids, values);
    }
}

contracts/certificates/RubyScorePortalCl.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.21;

import {AbstractPortal} from "./abstracts/AbstractPortal.sol";
import {AttestationPayload} from "./interfaces/Structs.sol";

/**
 * @title Default Portal
 * @author Consensys
 * @notice This contract aims to provide a default portal
 * @dev This Portal does not add any logic to the AbstractPortal
 */
contract RubyScorePortalClean is AbstractPortal {
    bytes32 schema;

    /**
     * @notice Contract constructor
     * @param modules list of modules to use for the portal (can be empty)
     * @param router the Router's address
     * @dev This sets the addresses for the AttestationRegistry, ModuleRegistry and PortalRegistry
     */
    constructor(address[] memory modules, address router) AbstractPortal(modules, router) {}

    function setSchema(bytes32 _schema) external {
        schema = _schema;
    }

    //TODO: only owner
    function addModule(address module) external {
        modules.push(module);
    }

    //TODO: only owner
    function removeModules() external {
        delete modules;
    }

    struct AttestationRequestData {
        uint256 level;
        bool onchain;
    }

    // struct AttestationPayload {
    //     bytes32 schemaId; // The identifier of the schema this attestation adheres to.
    //     uint64 expirationDate; // The expiration date of the attestation.
    //     bytes subject; // The ID of the attestee, EVM address, DID, URL etc.
    //     bytes attestationData; // The attestation data.
    // }

    function attestScoreSimple(uint64 level, bool onchain, bytes memory signature) public payable {
        AttestationRequestData memory attestationRequestData = AttestationRequestData(level, onchain);
        bytes[] memory validationPayload = new bytes[](1);
        validationPayload[0] = signature;
        attestScore(attestationRequestData, validationPayload);
    }

    function attestScore(
        AttestationRequestData memory attestationRequestData,
        bytes[] memory validationPayload
    ) internal {
        bytes memory attestationData = abi.encodePacked(attestationRequestData.level, attestationRequestData.onchain);
        AttestationPayload memory attestationPayload = AttestationPayload(
            schema,
            1767786495,
            abi.encode(msg.sender),
            attestationData
        );
        super.attest(attestationPayload, validationPayload);
    }

    function bulkAttest0xScore(
        AttestationRequestData[] memory attestationsRequests,
        bytes[] memory validationPayload
    ) public {
        for (uint256 i = 0; i < attestationsRequests.length; i++) {
            attestScore(attestationsRequests[i], validationPayload);
        }
    }

    /// @inheritdoc AbstractPortal
    function withdraw(address payable to, uint256 amount) external override {}
}

